import React, { useState, useEffect } from 'react';
import firebase from '../../utils/firebaseUtils';

function Waiter () {
  const [menu, setMenu] = useState([]);

  useEffect(() => {
    const allMenu = firebase.firestore()
    .collection('menu')
    .get()
    .then(querySnapshot => {querySnapshot.forEach(doc => {
      setMenu(currentVal => [...currentVal, doc.data()])})})
  },
  []
);

  return (
    <>
    <Input />
    <h1 id="waiter">Card√°pio</h1>
    <ul>
    {menu.forEach(doc => (
      <li>{doc.id}
      {doc.name}
      {doc.price}
      </li>
    ))}
    </ul>
  </>
);
}

export default Waiter
