import React, { useState, useEffect } from 'react';
import firebase from '../../utils/firebaseUtils';
import './styles.css';
import Input from '../../components/Input';
import Button from '../../components/Button'

function Waiter () {
  const [menu, setMenu] = useState([]);

  useEffect(() => {
    firebase.firestore()
    .collection('menu')
    .get()
    .then(querySnapshot => {
      const newMenu = querySnapshot.docs.map((doc) => ({
      ...doc.data()}))
      console.log(newMenu)
      setMenu(newMenu)
    })
  },
  []
);

  return (
    <section id="all-menu">
    <h1 id="waiter">Cardápio</h1>
    <Input label="Nome do cliente: "/>
    <ul id="items-list">
      {menu.map(doc => (
        <section id="item-card">
        <p key={doc.id}></p>
        <h3>{doc.name}</h3>
        <p>Quantidade:</p><button>-</button><button>+</button>
        <p>Preço: R${doc.price},00</p>
        </section>
      ))}
    </ul>
    <Button name="Salvar pedido"/>
    </section>
  )
}

export default Waiter
