import React, { useState, useEffect } from 'react';
import firebase from '../../utils/firebaseUtils';
import './styles.css';
import Input from '../../components/Input';
import Button from '../../components/Button';
import Header from '../../components/Header';

function Waiter () {
  const [menu, setMenu] = useState([]);
  const [order, setOrder] = useState([]);
  const [client, setClient] = useState('');
  const [table, setTable] = useState('');
  const [total, setTotal] = useState(0);
  const [breakfast, setBreakfast] = useState([]);
  const [lunch, setLunch] = useState([]);

  useEffect(() => {
  firebase
    .firestore().collection('menu').where('breakfast', '==', 'true')
    .get().then(snapshot => {
      snapshot.forEach(doc => {
        setBreakfast((currentState) => [...currentState, doc.data()])
      })
    })
  firebase
    .firestore().collection('menu').where('breakfast', '==', 'false')
    .get().then(snapshot => {
      snapshot.forEach(doc => {
        setLunch((currentState) => [...currentState, doc.data()])
      })
    })
}, [])

function breakfastMenu () {
  return breakfast.map(doc => (
    <section id="item-card">
      <p key={doc.id}></p>
      <h3>{doc.name}</h3>
      <div>Quantidade:
      <Button id="quant-btn" name="-" handleClick={() => subtractItem(doc)} />
      { order.map(elem => elem.name === doc.name? elem.quantity : false)}
      <Button id="quant-btn" name="+" handleClick={() => addItem(doc)} />
    </div>
    <p>Preço: R${doc.price},00</p>
  </section>
))
}
